"use client";
import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Button } from '@mui/material';
import { useFormik } from 'formik';


export default function Forms(props) {


  const validate = values => {
    const errors = {};

    if (!values.name) {
      errors.name = 'Name is required';
    }

    if (!values.email) {
      errors.email = 'Email is required';
    }

    if (!values.phone_number) {
      errors.phone_number = 'Phone number is required';
    }  

    return errors;
  };

  const formik = useFormik({
    enableReinitialize:true,
    initialValues: {
      name: props.user.name,
      email: props.user.email,
      phone_number: props.user.phone_number,
    },
    validate,

    onSubmit:  (values,  { setSubmitting, resetForm }) => {
      {props.isMode=="Edit"?props.updateUser(props.user._id,values):props.createUser(values)}
      resetForm({});
    }
  });



  return (
    <>
      <h3 style={{textAlign:'center',paddingTop:"15px",marginTop:"15px"}}>{props.isMode} User</h3>
      <Box
        component="form"
        sx={{
          '& > :not(style)': { m: 1, width: '100%' },
          pl: 10,pr: 10
        }}
        noValidate
        autoComplete="off"
        onSubmit={formik.handleSubmit}
      >
        <TextField id="name" 
          label="Name" 
          name='name'
          type="text"
          onChange={formik.handleChange}
          value={formik.values.name} 
          variant="outlined" 
          disabled={props.isMode=="Add"?true:false}          
          />
        {formik.errors.name ? <span  style={{color:"red"}}>{formik.errors.name}</span> : null}

        <TextField id="email" 
          label="Email" 
          name='email'
          type="email"
          onChange={formik.handleChange}
          value={formik.values.email} 
          variant="outlined" />
        {formik.errors.email ? <span  style={{color:"red"}}>{formik.errors.email}</span> : null}

        <TextField
            id="phone_number"
            name="phone_number"
            label="Phone Number"
            type="number"
            onChange={formik.handleChange}
            value={formik.values.phone_number} 
            variant="outlined"
          />
          {formik.errors.phone_number ? <span style={{color:"red"}}>{formik.errors.phone_number}</span> : null}
          {props.isMode=="Edit"?<><Button variant="contained" color="success" type="submit">
            Update User
          </Button><Button color="success" type="button" onClick={()=>{
            props.setIsMode("Add");  props.setUser({
                "name":'',
                "email":'',
                "phone_number":"",
              })}}>
            Back
          </Button></>:""}
          {props.isMode=="Add"?<Button variant="contained" color="success" type="submit">
            Add User
          </Button>:""}
          {props.isMode=="View"?<>
            <Button color="success" type="button" onClick={()=>{
              props.setIsMode("Add");  props.setUser({
                  "name":'',
                  "email":'',
                  "phone_number":"",
                })}}>
              Back
            </Button></>:""}
      </Box>
    </>
  );
}
