import React, { useState,useEffect } from 'react'
import { Grid } from '@mui/material';
import Forms from '../Forms';
import Tables from '../Tables';
import ConformationBox from '../ConformationBox';
import axios from "axios";
import Pagination from '@mui/material/Pagination';
import Stack from '@mui/material/Stack';

export default function Home() {
  const [user,setUser]=useState({
              "name":'',
              "email":'',
              "phone_number":"",
            })
  const [users,setUsers]=useState([]);

  const [edit, setEdit] = useState(false);
  const [open, setOpen] = useState(false);
  const [pageCount, setpageCount] = useState(2);
  const [selectedUserid, setselectedUserid] = useState("");

  const handleClickOpen = (id) => {
    setselectedUserid(id)
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const deleteUser = async () => {
    await axios.delete("http://localhost:3001/users/"+selectedUserid).then((response) => {
        setUser({
          "name":'',
          "email":'',
          "phone_number":"",
        })
        setEdit(false)
        handleClose()
        setselectedUserid("")
    });    
  };

  const handleClickEditOpen =async (id) => {

    await axios.get("http://localhost:3001/users/"+id)
    .then((response) => {
        setUser(response.data.data)
    });
    setEdit(true)
  };

  const getAllUsers = async(page=1) =>{
    await axios.get("http://localhost:3001/usersList?page="+page).then((response) => {
        setpageCount(response.data.count) 
        setUsers(response.data.list);
    });
  }   

  const updateUser = async(id,userData) =>{

      await axios({
        method: "patch",
        url: "http://localhost:3001/users/"+id,
        data: userData,
      })
      .then((response) => {
          if(response.status===204){
            setEdit(false);
            setUser({
              "name":'',
              "email":'',
              "phone_number":"",
            });
          }        
      });
  }  

  const createUser = async (userData) =>{

      await axios({
        method: "post",
        url: "http://localhost:3001/users",
        data: userData,
      })
      .then((response) => {
        if(response.status===201){
          setEdit(false);
          setUser({
            "name":'',
            "email":'',
            "phone_number":"",
          });
          console.log(user);
          }        
      });
  } 

  const [page, setPage] = useState(1);

  const handleChange = (event, newPage) => {
    console.log(newPage)
    setPage(newPage);
    getAllUsers(newPage);
  };

  useEffect(() => {
    getAllUsers();
  }, [user]); // <- add the count variable here

  return(
    <>
        <h2 style={{textAlign:'center'}}>Home Page</h2>
        <Grid container >

        <ConformationBox open={open} setOpen={setOpen}  handleClose={handleClose} deleteUser={deleteUser}/>
        <Grid item xs={7} style={{borderRight: '4px solid '}}  justifyContent="center" alignItems="center">
            <Tables users={users} setUsers={setUsers} handleClickOpen={handleClickOpen} handleClickEditOpen={handleClickEditOpen}/>
            <Stack spacing={2} style={{float:'right'}}>

                <Pagination color="primary" 
                    count={pageCount} page={page} onChange={handleChange} 
                />

            </Stack>
        </Grid>
        <Grid item xs={5}  justifyContent="center" alignItems="center"> 
            <Forms  users={users} setEdit={setEdit} edit={edit} updateUser={updateUser} createUser={createUser} setUsers={setUsers} user={user} setUser={setUser}/>
        </Grid>

        </Grid>
    </>
  );

}
