import React, { useState } from 'react'
import {
  BrowserRouter as Router,
  Route,
  Routes
} from "react-router-dom";
import Home from "./components/Screens/Home";
import About from "./components/Screens/About";
import Contact from "./components/Screens/Contact";
import NavBar from './components/NavBar';
import { Alert, Grid } from '@mui/material';
import {UserContext} from "./context/UserContext";

const App = () => {

  const [isSuccessError, setIsSuccessError] = useState({
            "status":false,
            "message":"User added successfully.",
            "isSuccess":true,
        });
        console.log(isSuccessError)
  return (
    <>
    <Router>
      <UserContext.Provider value="">
        {/* {isSuccessError['status']===true?<Alert severity="success" style={{width:'17%',position:'absolute',right:15,bottom:15,zIndex:9999}}></Alert>:""} */}
        {/* {isSuccessError['status']===true?<Alert severity={isSuccessError.isSuccess?"success":"error"} style={{width:'17%',position:'absolute',right:15,bottom:15,zIndex:9999}}>{isSuccessError.message}.</Alert>:""} */}
        <NavBar/>
        <Grid style={{marginTop:'70px'}}>
          <Routes>          
            <Route path='/' exact element={<Home/>} />
            <Route path='/about' element={<About/>} />
            <Route path='/contact' element={<Contact/>} /> 
          </Routes>        
        </Grid>
      </UserContext.Provider>
    </Router>
    </>
  )
}

export default App;
