import React, { useState,useEffect } from 'react'
import { Grid } from '@mui/material';
import Forms from './components/Forms';
import Tables from './components/Tables'
import ConformationBox from './components/ConformationBox';
import axios from "axios";
// import { Routes, Route, Outlet, Link } from "react-router-dom";

export default function Home() {
  const [user,setUser]=useState({
              "name":'',
              "email":'',
              "phone_number":"",
            })
  const [users,setUsers]=useState([]);

  const [edit, setEdit] = useState(false);
  const [open, setOpen] = useState(false);
  const [selectedUserid, setselectedUserid] = useState("");

  const handleClickOpen = (id) => {
    setselectedUserid(id)
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const deleteUser = async () => {
    await axios.delete("http://localhost:3001/users/"+selectedUserid).then((response) => {
        setUser({
          "name":'',
          "email":'',
          "phone_number":"",
        })
        setEdit(false)
        handleClose()
        setselectedUserid("")
    });    
  };

  const handleClickEditOpen =async (id) => {

    await axios.get("http://localhost:3001/users/"+id)
    .then((response) => {
        setUser(response.data.data)
    });
    setEdit(true)
  };

  const getAllUsers = async() =>{
    await axios.get("http://localhost:3001/usersList").then((response) => {
      setUsers(response.data);
    });
  }   

  const updateUser = async(id,userData) =>{

      await axios({
        method: "patch",
        url: "http://localhost:3001/users/"+id,
        data: userData,
      })
      .then((response) => {
          if(response.status===204){
            setEdit(false);
            setUser({
              "name":'',
              "email":'',
              "phone_number":"",
            });
          }        
      });
  }  

  const createUser = async (userData) =>{

      await axios({
        method: "post",
        url: "http://localhost:3001/users",
        data: userData,
      })
      .then((response) => {
        if(response.status===201){
          setEdit(false);
          setUser({
            "name":'',
            "email":'',
            "phone_number":"",
          });
          console.log(user);
          }        
      });
  } 
  useEffect(() => {
    getAllUsers();
  }, [user]); // <- add the count variable here

  return(
    <Grid container >
      <ConformationBox open={open} setOpen={setOpen}  handleClose={handleClose} deleteUser={deleteUser}/>
      <Grid item xs={7} style={{borderRight: '4px solid '}}  justifyContent="center" alignItems="center">
        <Tables users={users} setUsers={setUsers} handleClickOpen={handleClickOpen} handleClickEditOpen={handleClickEditOpen}/>
      </Grid>
      <Grid item xs={5}  justifyContent="center" alignItems="center"> 
        <Forms  users={users} setEdit={setEdit} edit={edit} updateUser={updateUser} createUser={createUser} setUsers={setUsers} user={user} setUser={setUser}/>
      </Grid>

    </Grid>
  );

}
